mapper.select:
SELECT *
FROM story b
WHERE b.id IN

    #{id}

AND b.state=#{status}

    order by #{sortRule.rule.code} #{sortRule.order.code}

SELECT b.id
FROM story b
WHERE b.writer_id = #{writerId}
  AND b.title = #{title}

SELECT *
FROM story b
WHERE b.id = #{value}

SELECT b.id
FROM story b
WHERE b.writer_id = #{value}

SELECT count(*)
FROM story
WHERE writer_id = #{writerId}
  AND state = #{state}

SELECT writer_id, state, title, content, summary, release_date, nearest_modify_date
FROM story
WHERE writer_id = #{writerId}

SELECT id
FROM story
WHERE writer_id = #{value}
